import * as React from 'react';
import { useTheme } from '@mui/material/styles';
import Typography from '@mui/material/Typography';
import Title from './Title';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

function preventDefault(event) {
  event.preventDefault();
}

const names = [
  'Oliver Hansen',
  'Van Henry',
  'April Tucker',
  'Ralph Hubbard',
  'Omar Alexander',
  'Carlos Abbott',
  'Miriam Wagner',
  'Bradley Wilkerson',
  'Virginia Andrews',
  'Kelly Snyder',
];

function getStyles(name, personName, theme) {
  return {
    fontWeight:
      personName.indexOf(name) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

export default function Deposits(props) {

  // we are going to have filters to filter by branch etc.
  // we need to call the backend to populate all the different branches and accounts
  // the onChange function will need to query the db to get the aggregated r
  const {title, sourceType} = props
  const theme = useTheme();
  const [source, setSource] = React.useState([]);
  const [amount, setAmount] = React.useState([]);


  const handleChange = (event) => {
    const {
      target: { value },
    } = event;
    setSource(
      // On autofill we get a stringified value.
      typeof value === 'string' ? value.split(',') : value,
    );
    // make the call function to the backend to calculate the amount.
  };


  return (
    <React.Fragment>
      <Title>{title}</Title>
      <Typography component="p" variant="h4">
        {amount}
      </Typography>
      <div>
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">{sourceType}</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={source}
          label={sourceType}
          onChange={handleChange}
        >
          {names.map((name) => (
            <MenuItem
              key={name}
              value={name}
              style={getStyles(name, source, theme)}
            >
              {name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      </div>
    </React.Fragment>
  );
}
